mapper.insert:
INSERT INTO PLAN_TB (
	  PLAN_ID
	, TITLE
	, PLAN_START_DATE
	, PLAN_END_DATE
	, COMPANION_TYPE_CD
	, STATE
	, CREATE_ID
	, CREATE_DT
	, UPDATE_ID
	, UPDATE_DT
) VALUES (
	  #{planId}
	, #{title}
	, #{planStartDate}
	, #{planEndDate}
	, #{companionTypeCd}
	, '1'
	, #{createId}
	, NOW()
	, #{createId}
	, NOW()
)

mapper.update:
UPDATE PLAN_TB
   SET STATE = '9'
 WHERE PLAN_ID = #{planId}

UPDATE PLAN_TB
   SET TITLE = #{title}
     , UPDATE_ID = #{updateId}
     , UPDATE_DT = NOW()
 WHERE PLAN_ID = #{planId}

UPDATE PLAN_TB
   SET RPSNT_IMAGE_URL = #{rpsntImageUrl}
     , UPDATE_ID = #{updateId}
     , UPDATE_DT = NOW()
 WHERE PLAN_ID = #{planId}
