mapper.insert:
INSERT INTO PLAN_MEMBER_TB (
	  PLAN_ID
	, SEQ_NO
	, CUSTOMER_ID
	, PLAN_JOIN_DT
	, AUTH
) VALUES (
	  #{planId}
	, (SELECT NVL(MAX(SEQ_NO), 0) + 1 FROM PLAN_MEMBER_TB X WHERE PLAN_ID = #{planId})
	, #{customerId}
	, SYSDATE
	, #{auth}
)

mapper.update:
UPDATE PLAN_MEMBER_TB
   SET PLAN_LEAVE_DT = NOW()
 WHERE PLAN_ID = #{planId}
   AND CUSTOMER_ID = #{customerId}

UPDATE PLAN_MEMBER_TB
   SET AUTH = #{auth}
 WHERE PLAN_ID = #{planId}
   AND CUSTOMER_ID = #{customerId}
